import React, { useState } from "react";

export default function CounterApp() {
  const [count, setCount] = useState(0);

  const handleIncrement = () => {
    setCount(+1);
  };

  const handleDecrement = () => {
    setCount(-1);
  };

  return (
    <>
      <button type="button" style={{ marginLeft: 50 }} className="increment">
        Increment{handleIncrement}
      </button>
      <h3 style={{ margin: 50 }}>value: {count}</h3>
      <button type="button" style={{ marginLeft: 50 }} className="decrement">
        Decrement{handleDecrement}
      </button>
      <button type="button" style={{ marginLeft: 50 }} className="reset">
        Reset
      </button>
    </>
  );
}
