import React, { useState } from 'react'



export default function Input({
    placeHolderKeyword,
    onChange, 
    label,
    type="text",
    }){
    return(
        <div >
            <label 
            style={{
                fontSize:"0.8rem",
                opacity: 0.7,
                marginBottom:5,
                display:"block",
                }}>
                {label}
            </label>

            
            <Input
            style={{marginBottom:20}}
            type={type}
            placeholder={`Enter your ${placeHolderKeyword}`}
            onChange={onChange}
            />

            
        </div>
    );
}
