import React, { useState } from "react";

export default function CounterApp() {
  const [count, setCount] = useState(0);

  const handleIncrement = () => {
    setCount(count + 1);
  };

  const handleDecrement = () => {
    if (count <= 0) {
      setCount(0);
    } else {
      setCount(count - 1);
    }
  };

  const resetValue = () => {
    setCount(0);
  };

  return (
    <>
      <button onClick={handleIncrement} type="button" className="increment">
        Increment
      </button>

      <h3 style={{ margin: 50 }}>value: {count}</h3>

      <button onClick={handleDecrement} type="button" className="decrement">
        Decrement
      </button>

      <button onClick={resetValue} type="button" className="reset">
        Reset
      </button>
    </>
  );
}
